/* eslint-disable react/prop-types */
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Button from '@mui/material/Button';
import LoginButton from './LoginButton';
import LogoutButton from './LogoutButton';
import { useAuth0 } from '@auth0/auth0-react';
import { useEffect } from 'react';

export default function Navbar({ userRole, setUserRole }) {
	const { user, isAuthenticated, isLoading } = useAuth0();

	useEffect(() => {
		if (isLoading || !isAuthenticated) {
			return;
		}
		var myHeaders = new Headers();
		myHeaders.append('Accept', 'application/json');
		myHeaders.append(
			'Authorization',
			'Bearer eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCIsImtpZCI6Im9SVl9LQTRaX1VmRnFWQmtTNGFMVyJ9..jSlmcyQ1PMbHjTgbkfqp8rS63TnZSdsQVwMIOG8yI_AZc94I9CEbR2tIB4-Y9VEF_KN88P_b-S66ecCUw1zZIlI-9ALMt9h_h1nyrlo2HTP8aZoUZ-mOYyVyIXvUqLlkIRarGsvQsWqpomtl8Gh52OCIc9UvTSIcVFxK0NJ2zv2C51u8-HU1Xx2PvIiSvVaaYBVzUMrDzcaHWWfQZTo8CHRE_a9hgyzuNqx1GvXKnmcIvb3GmezO2NOJIXbmdbrAJ69l-MAhnN30K1iwaDkfuYP0_KOlzmq3BWj8JTAOaoO0T6ir8rhANFKaczRRUrnSE0ukPzGKt5hih-V2BHx_TA'
		);

		var requestOptions = {
			method: 'GET',
			headers: myHeaders,
			redirect: 'follow',
		};

		fetch(
			'https://dev-hbz8z1qco74m4737.us.auth0.com/api/v2/roles/rol_73zk03NBdD7qLKXD/users',
			requestOptions
		)
			.then((response) => response.json())
			.then((result) => {
				// iterate through the users array and check if the user is in the array
				let role = '';
				result.forEach((element) => {
					if (element.user_id === user.sub) {
						role = 'admin';
					}
				});

				if (role === '') {
					setUserRole('user');
				} else {
					setUserRole(role);
				}
			})
			.catch((error) => console.log('error', error));
	}, [user, isLoading, isAuthenticated, setUserRole, userRole]);

	return (
		<AppBar sx={{ height: '7vh' }}>
			<Toolbar
				sx={{
					display: 'flex',
					justifyContent: 'flex-end',
				}}
			>
				{!isAuthenticated && <LoginButton />}
				{isAuthenticated && <LogoutButton />}
				{!isLoading && isAuthenticated && (
					<Button
						color='inherit'
						endIcon={
							<img
								src={user.picture}
								alt={user.name}
								style={{
									borderRadius: '50%',
									width: '30px',
									height: '30px',
								}}
							/>
						}
					>
						{user.profile}
					</Button>
				)}
			</Toolbar>
		</AppBar>
	);
}
